exploreUrl:
@js:
result=[
  {
    'title': '>> 个人中心 <<',
    'url': "",
    'style':{
      layout_flexGrow:1,
      layout_flexBasisPercent:1
    }
  },
  {
    'title': "我的收藏",
    'url': `/my/favorite`,
    'style':{
      layout_flexGrow:2,
      layout_flexBasisPercent:0.25
    	}
  },
  {
    'title': "观看记录",
    'url': `/my/view`,
    'style':{
      layout_flexGrow:2,
      layout_flexBasisPercent:0.25
    	}
  }
]
html=org.jsoup.Jsoup.parse(java.ajax(source.bookSourceUrl+"/list-01/"))
categorys=html.select("#category option")
sortings=html.select("#sorting option")
categorys.forEach(category => {
  result.push({
    'title': '>> ' + java.t2s(category.text()) + ' <<',
    'url': "",
    'style':{
      layout_flexGrow:1,
      layout_flexBasisPercent:1
    }
  })
  sortings.forEach(sorting => {
    result.push({
      'title': java.t2s(sorting.text()),
      'url':`/list-${category.attr('value')}${sorting.attr('value')}/{{page}}.html`,
      'style':{
        layout_flexGrow:2,
        layout_flexBasisPercent:0.25
      	}
    })
  })
})
JSON.stringify(result)

